import React, {useState, useEffect} from 'react';
import './Restaurant.css';

function Restaurant(props){
    const [restaurant, setRestaurant] = useState(0);

    useEffect(() => {
        setRestaurant(props.restaurant);
    }, [props.restaurant])


    function formatTransactions(transactions){
        var transaction_list = transactions.split(',');
        var transaction_str = "";
        var transaction;
        console.log("transactions:" + transactions);
        for (transaction in transaction_list){
            console.log("transaction:" + transaction);
            if (transaction === "restaurant_reservation"){
                transaction_str += "Dine-in\n";
            }
            else if (transaction === "delivery"){
                transaction_str += "Delivery\n";
            }
            else if (transaction === "pickup"){
                transaction_str += "Pickup\n";
            }
        }
        return transaction_str;
    }

    return (
        <div className = "Restaurant">
            <div className = "image-container">
                <img src = {restaurant.imageSrc} alt = ''/>;
            </div>
            <h2>{restaurant.name}</h2>
            <div className = "restaurant-info">
                <div className = "restaurant-address">
                    <p>{restaurant.address}</p>
                    <p>{restaurant.city}</p>
                    <p>{restaurant.state},{restaurant.zipCode}</p>
                </div>
                <div className = "restaurant-info2">
                    <p>{restaurant.category}</p>
                    <p>{formatTransactions(restaurant.transactions)}</p>
                    <p>{restaurant.rating}</p>
                </div>
            </div>
        </div>
    );
}

export default Restaurant;
